mapper.select:

  		select b.bno,b.title,b.content,b.writer,b.regdate,b.viewcnt
  		from 
	  		(select rownum as rnum,a.bno,a.title,a.content,a.writer,a.regdate,a.viewcnt
		  		from (
		  			select *
		  			from tbl_board
		  			order by bno desc
		  		) a
	  		where rownum <= #{end_page}) b
  		where b.rnum >= #{start_page}
